import React, { useState } from 'react';

function Cart()
{
    const [email, ChangeEmail] = useState('');
    const [password, ChangePassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    async function CheckButton(event) {

        event.preventDefault();

        //donnée a tester par l'api post
        const donnee_a_verifier = { email: email, password: password }

        let response = await fetch("http://localhost:7005/api/show_cart",
            {
                method: 'POST',
                headers:
                {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(donnee_a_verifier)
            });

        // analyse si l'api répond
        if (!response.ok) {
            throw new Error('Network response was not ok');
        }

        // stockage de la réponse
        let result = await response.json();
    }
    
    return(
        <div class="container"> 
            
            <div class="login-box">

                <div class="box-container">

                    <h1>Panier</h1>

                </div>

            </div>


        </div>

    )
}

export default Cart;
