import React, {useState} from "react"
import { Link } from "react-router-dom"

function Menu()
{
  const [connecter, setConnecter] = useState(localStorage.getItem('est_connecter') === 'true');
  const [prof, setProf] = useState(localStorage.getItem('role_utilisateur') === 'prof');

  function deconnexion(){
    localStorage.removeItem('est_connecter')
    localStorage.removeItem('role_utilisateur')
    setProf(false)
    setConnecter(false)
  }
    return(
        <div> 
<nav>
<ul>

  <li>
    <Link to ="/">Accueil</Link>
  </li>

  <li>
    <Link to ="/form1">Magasins</Link>
  </li>
  <li>
    <Link to ="/form2">Articles</Link>
  </li>


      {connecter ? (
    <li><Link to ="/" onClick={deconnexion}>DÃ©connexion</Link></li>

    ) : (
        <li><Link to ="/connexion">Connexion</Link></li>

      )}
    
    {connecter && prof && <li><Link to ="/etudiants">Etudiants</Link></li>}
    {connecter && !prof && <li><Link to ="/etudiants">Mes notes</Link></li>}

    {connecter && prof &&  <li>
    <Link to ="/profils">Utilisateurs</Link>
  </li>}
  
  
</ul>


</nav>


        </div>


    )
}

export default Menu;
