import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

import './App.css';
import Menu from './pages/menu';
import Connexion from './pages/connexion';
import Accueil from './pages/accueil';
import Form1 from './pages/Form1';
import Form2 from './pages/form2';
import Profils from './pages/profil';
import Etudiants from './pages/etudiants';


function App() {

  const [connecter ] = useState(localStorage.getItem('est_connecter') === 'true');
  const [prof ] = useState(localStorage.getItem('est_connecter') === 'true');

  var poudre = "Hey les mecs je crois que le logo a disparu..";
  var fred = [
  {
    ordi: "mac",
    price: 666
  },
  {
    ordi: "Lenovo",
    price: 1
  },
  {
    ordi: "Machine à écrire",
    price: 4
  },
  {
    ordi: "Linux",
    price: 1772
  }
  ]

  return (
   <Router>
    <div>
    <Menu />
        <p>{poudre}</p>


        <table>
          {
            fred.map((item,index) => {
              return <tr key={index}>
                <td>Marque : {item.ordi}</td>
                <td>Prix : {item.price}€</td>
              </tr>
            })
          }
        </table>

        <Routes>
            <Route path='/form1' element={<Form1/>} />
            <Route path='/form2' element={<Form2/>} />
            <Route path="/connexion" element={<Connexion/>} />
            {connecter && prof && <Route path='/profils' element={<Profils/>}/>}
            {connecter && prof && <Route path='/etudiants' element={<Etudiants/>}/>}
            <Route path='/accueil' element={<Accueil/>} />
            <Route path='/' element={<Accueil/>} />
          </Routes> 

    </div>
    </Router>
  );
}

export default App;
