    }

    return (
        <div class="container ta-center spacement">
            <h1>Retrouvez tous nos magasins</h1>

            <p>Vous cherchez le magasin le plus près de chez vous ? Cosultez la liste de tous nos magasins installés en France.</p>


            
            <div>

            {donnees === null ? 
                (
                <p>Chargement en cours...</p>
                ) : 
            (
               <div class="cards-container">
                {donnees.map((element, index) => (
                            <div class="article-card" key={index}>

                                <div>

                                <h1>{element.article_name}</h1>

                                <h2>Disponible chez : <b>{element.shop_name} (A {element.city_name} )</b></h2>

                                <p class="price">{element.price}€</p>

                                <button onClick={add_cart} class="buy">Ajouter au panier</button>

                            </div>

                            </div>
                        ))}
                </div>
            )}        
                

            </div>
           
        </div>
    )
}

export default Articles
