    <div>
      <nav>
        <div className="container sb">
          <ul>
            if(logged) {
              
            }
            <li>
              <Link to="/">Accueil</Link>
            </li>
            <li>
              <Link to="/shops">Magasins</Link>
            </li>
            <li>
              <Link to="/articles">Articles</Link>
            </li>
          </ul>
          <div>
            <li><Link to="/cart">Panier</Link> ({numItems})</li>
          </div>
        </div>
      </nav>
    </div>
  );
}

export default Menu;
