import React, {useState, useEffect} from "react"
import { Link } from "react-router-dom"

function Menu(props)
{

  const [numItems, setNumItems] = useState(0);

  const add_cart = (item) => {
    // On vérifie si l'article est déjà présent dans le panier
    const existingItem = cart.find((cartItem) => cartItem.id === item.id);
  
    // Si l'article est déjà présent, on incrémente la quantité
    if (existingItem) {
      setCart(
        cart.map((cartItem) =>
          cartItem.id === item.id
            ? { ...cartItem, quantity: cartItem.quantity + 1 }
            : cartItem
        )
      );
    } else {
      // Sinon, on ajoute le nouvel article au panier
      setCart([
        ...cart,
        {
          id: item.id,
          article_name: item.article_name,
          shop_name: item.shop_name,
          city_name: item.city_name,
          price: item.price,
          quantity: 1,
        },
      ]);
    }
  
    // On met à jour le localStorage avec le nouveau panier
    localStorage.setItem("cart", JSON.stringify(cart));
  };

    return(
        <div> 
<nav>

  <div class="container sb">
  
      <ul>

        <li>
          <Link to ="/">Accueil</Link>
        </li>

        <li>
          <Link to ="/shops">Magasins</Link>
        </li>
        <li>
          <Link to ="/articles">Articles</Link>
        </li>
        
      </ul>

      <div>

            <li><a href="/cart">Panier</a> ({numItems})</li>

      </div>

  </div>

</nav>

        </div>

    )
}

export default Menu;
