import React, { useState, useEffect } from 'react';

function Articles() {

    const [donnees, setDonnees] = useState(null);

    useEffect(() => {
      async function fetchDonnees() {
        const response = await fetch('http://localhost:7005/api/show_articles');
        const donnees = await response.json();

        console.log(donnees)
        setDonnees(donnees);
      }
      fetchDonnees();
    }, []);

    const [cart, setCart] = useState([]);

    const add_cart = (item) => {
      // On vérifie si l'article est déjà présent dans le panier
      const existingItemIndex = cart.findIndex((cartItem) => cartItem.id === item.id);
    
      // Si l'article est déjà présent, on incrémente la quantité
      if (existingItemIndex !== -1) {
        const updatedCart = cart.map((cartItem, index) => {
          if (index === existingItemIndex) {
            return {
              ...cartItem,
              quantity: cartItem.quantity + 1,
            };
          } else {
            return cartItem;
          }
        });
        setCart(updatedCart);
        localStorage.setItem("cart", JSON.stringify(updatedCart));
      } else {
        // Sinon, on ajoute le nouvel article au panier
        const updatedCart = [
          ...cart,
          {
            id: item.id,
            name: item.name,
            price: item.price,
            quantity: 1,
          },
        ];
        setCart(updatedCart);
        localStorage.setItem("cart", JSON.stringify(updatedCart));
      }
    };

    
    return (
        <div class="container ta-center spacement">
            <h1>Retrouvez tous nos magasins</h1>

            <p>Vous cherchez le magasin le plus près de chez vous ? Cosultez la liste de tous nos magasins installés en France.</p>

            <div>

            {donnees === null ? 
                (
                <p>Chargement en cours...</p>
                ) : 
            (
               <div class="cards-container">
                {donnees.map((element, index) => (
                            <div class="article-card" key={index}>

                                <div>

                                <h1>{element.article_name}</h1>

                                <h2>Disponible chez : <b>{element.shop_name} (A {element.city_name} )</b></h2>

                                <p class="price">{element.price}€</p>

                                <button onClick={() => add_cart(element)} class="buy">Ajouter au panier</button>

                            </div>

                            </div>
                        ))}
                </div>
            )}        
                

            </div>
           
        </div>
    )
}

export default Articles
