import React, { useState, useEffect } from 'react';

function Cart()
{

      const [donnees, setDonnees, calculateTotalPrice] = useState(null);

    const Cart = () => {
        const [cart, setCart] = useState([]);
      
        const removeFromCart = (articleToRemove) => {
          const updatedCart = cart.filter((article) => article.id !== articleToRemove.id);
          setCart(updatedCart);
        };
      
        const cartItems = cart.map((article) => (
          <div key={article.id}>
            <h2>{article.article_name}</h2>
            <p>Quantité : {article.quantity}</p>
            <p>Disponible chez : <b>{article.shop_name} (A {article.city_name})</b></p>
            <p>Prix unitaire : {article.price}€</p>
            <button onClick={() => removeFromCart(article)}>Retirer du panier</button>
          </div>
        ));
    
        const total = cart.reduce((acc, article) => acc + article.price * article.quantity, 0);

    return(
        <div class="container"> 

        <div class="row">

            <div class="col-7">
            
            <div class="box">

                    <h1>Panier</h1>

                    <hr/>

                    <ul class="cart">
                        <li>{cartItems}</li>
                    </ul>

            </div>

            </div>

            <div class="col-3">

            <div class="box">
            {cart.map((article) => (
                <p>Prix total : {calculateTotalPrice()}€</p>
            ))}

                <button class="buy">Passer à la caisse</button>

                </div>

            </div>

            </div>

        </div>

    );
}

}
export default Cart;
