import React, { useState, useEffect } from 'react';

function Articles() {

    const [donnees, setDonnees] = useState(null);

    useEffect(() => {
      async function fetchDonnees() {
        const response = await fetch('http://localhost:7005/api/show_articles');
        const donnees = await response.json();

        console.log(donnees)
        setDonnees(donnees);
      }
      fetchDonnees();
    }, []);

    const [cart, setCart] = useState([]);

    const add_cart = (article) => {
      let newCart = [...cart];
      let existingArticle = newCart.find((item) => item.id === article.id);
    
      if (existingArticle) {
        existingArticle.quantity += 1;
        existingArticle.totalPrice = existingArticle.price * existingArticle.quantity;
      } else {
        const newArticle = { ...article, quantity: 1, totalPrice: article.price };
        newCart.push(newArticle);
      }
    
      setCart(newCart);
      localStorage.setItem('cart', JSON.stringify(newCart));
    };

    return (
        <div class="container ta-center spacement">
            <h1>Retrouvez tous nos magasins</h1>

            <p>Vous cherchez le magasin le plus près de chez vous ? Cosultez la liste de tous nos magasins installés en France.</p>

            <div>

            {donnees === null ? 
                (
                <p>Chargement en cours...</p>
                ) : 
            (
               <div class="cards-container">
                {donnees.map((element, index) => (
                            <div class="article-card" key={index}>

                                <div>

                                <h1>{element.article_name}</h1>

                                <h2>Disponible chez : <b>{element.shop_name} (A {element.city_name} )</b></h2>

                                <p class="price">{element.price}€</p>

                                <button onClick={() => addToCart(element)} class="buy">Ajouter au panier</button>

                            </div>

                            </div>
                        ))}
                </div>
            )}        
                

            </div>
           
        </div>
    )
}

export default Articles
