import React, { useState, useEffect } from 'react';
import { add_cart } from './articles';

function Cart()
{

    const [cart, setCart] = useState([]);

    useEffect(() => {
      const savedCart = JSON.parse(localStorage.getItem('cart')) || [];
      setCart(savedCart);
    }, []);
    const removeFromCart = (id) => {
        setCart(cart.filter((article) => article.id !== id));
      };
  
    const calculateTotalPrice = () => {
      return cart.reduce((total, article) => {
        return total + article.totalPrice;
      }, 0);
    };
    
    return(
        <div class="container"> 

        <div class="row">

            <div class="col-7">
            
            <div class="box">

                    <h1>Panier</h1>

                    <hr/>

                    <ul class="cart">
                    {cart.reduce((acc, article) => {
                        const existingIndex = acc.findIndex((item) => item.id === article.id);
                        if (existingIndex !== -1) {
                            acc[existingIndex].quantity += article.quantity;
                        } else {
                            acc.push(article);
                        }
                        return acc;
                        }, []).map((article) => (
                        <div key={article.id}>
                            <h2>{article.article_name}</h2>
                            <p>Quantité : {article.quantity}</p>
                            <p>Disponible chez : <b>{article.shop_name} (A {article.city_name})</b></p>
                            <p>Prix unitaire : {article.price}€</p>
                            <button onClick={() => removeFromCart(article.id)}>Retirer du panier</button>
                        </div>
                        ))}
                    </ul>

            </div>

            </div>

            <div class="col-3">

            <div class="box">


                <button class="buy">Passer à la caisse</button>

                </div>

            </div>

            </div>

        </div>

    );
}

export default Cart;
