import React, { useState, useEffect } from 'react';

function Cart()
{

    const [donnees, setDonnees] = useState(null);

    useEffect(() => {
      async function fetchDonnees() {
        const response = await fetch('http://localhost:7005/api/show_cart');
        const donnees = await response.json();

        console.log(donnees)
        setDonnees(donnees);
      }
      fetchDonnees();
    }, []);


    const [cart, setCart] = useState([]);

  useEffect(() => {
    const storedCart = localStorage.getItem('cart');
    if (storedCart) {
      setCart(JSON.parse(storedCart));
    }
  }, []);
    
    return(
        <div class="container"> 

        <div class="row">

            <div class="col-7">
            
            <div class="box">

                    <h1>Panier</h1>

                    <ul>
                    {cart.map((item, index) => (
  <div key={index}>
    <h2>{item.article_name}</h2>
    <p>Disponible chez : <b>{item.shop_name} (A {item.city_name})</b>
                        </p>
    <p>Prix unitaire : {item.price}€</p>
  </div>
))}
                    </ul>

            </div>

            </div>

            <div class="col-3">

            <div class="box">
            {cart.map((item, index) => (
                <p>Prix total : {item.totalPrice}€</p>
            ))}

                </div>

            </div>

            </div>

        </div>

    )
}

export default Cart;
