import React, { useState, useEffect } from 'react';

function Notes ({ id }) {

    const [donnees, setDonnees] = useState(null);


 
    useEffect(() => {


      async function fetchDonnees() {
        const response = await fetch(`http://localhost:7005/api/affiche_note/${id}`);
        const donnees = await response.json();

        setDonnees(donnees);
      }
      fetchDonnees();
    }, []);

    


  
    return (
        <div>


        {donnees === null ? 
        (
        <p>Chargement en cours...</p>
        ) : 
      (
        <table>
          {donnees.map((element, index) => (
                    <tr  key={index}>
                        <td>{element.matiere}</td>
                        <td>{element.note}/20</td>
                        <td>|| Coeff{element.coefficient}</td>
                    </tr>
                ))}
        </table>
      )}        
          
            
        </div>
    )
}

export default Notes
