import React, { useState, useEffect } from 'react';

function Cart() {

  const [cart, setCart] = useState(() => {
    const savedCart = JSON.parse(localStorage.getItem('cart')) || [];
    return savedCart;
  });

  useEffect(() => {
    async function fetchDonnees() {
      const response = await fetch('http://localhost:7005/api/show_cart');
      const donnees = await response.json();

      console.log(donnees);
      setCart(donnees);
    }
    fetchDonnees();
  }, []);

  useEffect(() => {
    const savedCart = JSON.parse(localStorage.getItem('cart')) || [];
    setCart(savedCart);
  }, []);

  const removeFromCart = (articleId) => {
    const updatedCart = cart.filter((item) => item.id !== articleId);
    setCart(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));
  };

  const calculateTotalPrice = () => {
    return cart.reduce((total, item) => {
      return total + item.totalPrice;
    }, 0);
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-7">
          <div className="box">
            <h1>Panier</h1>
            <hr />
            <ul className="cart">
              {cart.map((item, index) => (
                <div key={index}>
                  <h2>{item.article_name}</h2>
                  <p>
                    Disponible chez : <b>{item.shop_name} (A {item.city_name})</b>
                  </p>
                  <p>Prix unitaire : {item.price}€</p>
                  <button onClick={() => removeFromCart(item.id)}>Retirer du panier</button>
                </div>
              ))}
            </ul>
          </div>
        </div>
        <div className="col-3">
    <div className="box">
      <p>Prix total : {calculateTotalPrice()}€</p> {/* Calculer le prix total une seule fois */}
      <button className="buy">Passer à la caisse</button>
    </div>
  </div>
      </div>
    </div>
  );
}

export default Cart;
