import React, { useState, useEffect } from 'react';

function Articles() {

    const [donnees, setDonnees] = useState(null);

    useEffect(() => {
      async function fetchDonnees() {
        const response = await fetch('http://localhost:7005/api/show_articles');
        const donnees = await response.json();

        console.log(donnees)
        setDonnees(donnees);
      }
      fetchDonnees();
    }, []);

    const [cart, setCart] = useState([]);

    const add_cart = (item) => {
      const existingItem = cart.find((cartItem) => cartItem.id === item.id);
    
      if (existingItem) {
        // Si l'article est déjà présent dans le panier, on incrémente simplement la quantité
        existingItem.quantity += 1;
      } else {
        // Sinon, on ajoute le nouvel article au panier avec une quantité de 1
        const newItem = {
          id: item.id,
          article_name: item.article_name,
          shop_name: item.shop_name,
          city_name: item.city_name,
          price: item.price,
          quantity: 1,
        };
        setCart((prevState) => [...prevState, newItem]);
      }
    
      // On met à jour le localStorage avec le nouveau panier
      localStorage.setItem("cart", JSON.stringify(cart));
    };

    
    return (
        <div class="container ta-center spacement">
            <h1>Retrouvez tous nos magasins</h1>

            <p>Vous cherchez le magasin le plus près de chez vous ? Cosultez la liste de tous nos magasins installés en France.</p>

            <div>

            {donnees === null ? 
                (
                <p>Chargement en cours...</p>
                ) : 
            (
               <div class="cards-container">
                {donnees.map((element, index) => (
                            <div class="article-card" key={index}>

                                <div>

                                <h1>{element.article_name}</h1>

                                <h2>Disponible chez : <b>{element.shop_name} (A {element.city_name} )</b></h2>

                                <p class="price">{element.price}€</p>

                                <button onClick={() => add_cart(element)} class="buy">Ajouter au panier</button>

                            </div>

                            </div>
                        ))}
                </div>
            )}        
                

            </div>
           
        </div>
    )
}

export default Articles
