import React, {useState} from "react"
import { Link } from "react-router-dom"

function Menu()
{
  const [connecter, setConnecter] = useState(localStorage.getItem('est_connecter') === 'true');
  const [prof, setProf] = useState(localStorage.getItem('role_utilisateur') === 'prof');

  function deconnexion(){
    localStorage.removeItem('est_connecter')
    localStorage.removeItem('role_utilisateur')
    setProf(false)
    setConnecter(false)
  }
  
  const cart = JSON.parse(localStorage.getItem('cart')) || [];
  const numberOfItemsInCart = cart.length;

    return(
        <div> 
<nav>

  <div class="container sb">
  
      <ul>

        <li>
          <Link to ="/">Accueil</Link>
        </li>

        <li>
          <Link to ="/shops">Magasins</Link>
        </li>
        <li>
          <Link to ="/articles">Articles</Link>
        </li>

          {connecter && prof &&  <li>
          <Link to ="/profils">Utilisateurs</Link>
        </li>
        
        }

        
      </ul>

      <div>

            <li><a href="/cart">Panier</a> ({numberOfItemsInCart})</li>

      </div>

  </div>

</nav>

        </div>

    )
}

export default Menu;
