import React, { useState, useEffect } from 'react';

function Articles() {

    const [donnees, setDonnees] = useState(null);

    useEffect(() => {
      async function fetchDonnees() {
        const response = await fetch('http://localhost:7005/api/show_articles');
        const donnees = await response.json();

        console.log(donnees)
        setDonnees(donnees);
      }
      fetchDonnees();
    }, []);

    const [cartItems, setCartItems] = useState([]);

    function add_cart(article) {
      const existingItemIndex = cartItems.findIndex(item => item.id === article.id);
      if (existingItemIndex !== -1) {
        const updatedCartItems = [...cartItems];
        updatedCartItems[existingItemIndex].quantity += 1;
        setCartItems(updatedCartItems);
      } else {
        setCartItems([...cartItems, { ...article, quantity: 1 }]);
      }
    }
  
    function storeCartItems() {
      localStorage.setItem('cartItems', JSON.stringify(cartItems));
    }

    return (
        <div class="container ta-center spacement">
            <h1>Retrouvez tous nos magasins</h1>

            <p>Vous cherchez le magasin le plus près de chez vous ? Cosultez la liste de tous nos magasins installés en France.</p>


            
            <div>

            {donnees === null ? 
                (
                <p>Chargement en cours...</p>
                ) : 
            (
               <div class="cards-container">
                {donnees.map((element, index) => (
                            <div class="article-card" key={index}>

                                <div>

                                <h1>{element.article_name}</h1>

                                <h2>Disponible chez : <b>{element.shop_name} (A {element.city_name} )</b></h2>

                                <p class="price">{element.price}€</p>

                                <button onClick={() => add_cart({ id: element.id, name: element.article_name, price: element.price })} class="buy">Ajouter au panier</button>

                            </div>
                            </div>
                        ))}                </div>
            )}        
                

            </div>
           
        </div>
    )
}

export default Articles
