              ...article,
              shop_name: article.shop.name,
              city_name: article.shop.city.name
            }
          });
          setDonnees(newData);
      }
      fetchDonnees();
    }, []);


    return (
        <div class="container ta-center spacement">
            <h1>Retrouvez tous nos magasins</h1>

