import React, { useContext, useState } from "react";
import { Link } from "react-router-dom";
import { CartContext } from './CartContext';

function Menu() {
  const { cart } = useContext(CartContext);
  const numItems = cart ? cart.reduce((total, item) => total + item.quantity, 0) : 0;
  const [logged, setLogged] = useState(localStorage.getItem('logged_in') === 'true');

  function logout(){
    localStorage.removeItem('est_connecter')
    localStorage.removeItem('role_utilisateur')
    setProf(false)
    setConnecter(false)
  }

  if (logged) {

    return (
      <div>
        <nav>
          <div className="container sb">
            <ul>
              <li>
                <Link to="/">Accueil</Link>
              </li>
              <li>
                <Link to="/shops">Magasins</Link>
              </li>
              <li>
                <Link to="/articles">Articles</Link>
              </li>
            </ul>
            <div>
              <li><Link to="/cart">Panier</Link> ({numItems})</li>
            </div>
          </div>
        </nav>
      </div>
    );

  } else {
    return (
      <div>
        <nav>
          <div className="container sb">
            <ul>
              <li>
                <Link to="/">Accueil</Link>
              </li>
            </ul>
          </div>
        </nav>
      </div>
    );

  }
}

export default Menu;
