import React, { useState, useEffect } from 'react';

function Cart() {
  const [cart, setCart] = useState(null);

  useEffect(() => {
    async function fetchCartData() {
      try {
        const response = await fetch('http://localhost:7005/api/show_cart');
        const cartData = await response.json();
        setCart(cartData);
      } catch (error) {
        console.error(error);
      }
    }

    fetchCartData();
  }, []);

  useEffect(() => {
    const savedCart = JSON.parse(localStorage.getItem('cart')) || [];
    setCart(savedCart);
  }, []);

  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cart));
  }, [cart]);

  const addToCart = (articleToAdd) => {
    const existingArticleIndex = cart.findIndex((article) => article.id === articleToAdd.id);
    if (existingArticleIndex !== -1) {
      const updatedCart = [...cart];
      updatedCart[existingArticleIndex].quantity += 1;
      setCart(updatedCart);
    } else {
      const updatedCart = [...cart, { ...articleToAdd, quantity: 1 }];
      setCart(updatedCart);
    }
  };

  const removeFromCart = (articleId) => {
    const updatedCart = cart.filter((item) => item.id !== articleId);
    setCart(updatedCart);
  };

  const calculateTotalPrice = () => {
    return cart.reduce((total, item) => {
      return total + item.totalPrice;
    }, 0);
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-7">
          <div className="box">
            <h1>Panier</h1>
            <hr />
            {cart.length > 0 ? (
              <ul className="cart">
                {cart.map((item) => (
                  <div key={item.id}>
                    <h2>{item.article_name}</h2>
                    <p>
                      Disponible chez : <b>{item.shop_name} (A {item.city_name})</b>
                    </p>
                    <p>Prix unitaire : {item.price}€</p>
                    <button onClick={() => removeFromCart(item.id)}>Retirer du panier</button>
                  </div>
                ))}
              </ul>
            ) : (
              <p>Votre panier est vide.</p>
            )}
          </div>
        </div>
        <div className="col-3">
          <div className="box">
            <p>Prix total : {calculateTotalPrice()}€</p>
            <button className="buy">Passer à la caisse</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Cart;
